import React, { Component } from "react";
import "./App.css";
import {
  Link,
  NavLink,
  BrowserRouter,
  Route,
  withRouter
} from "react-router-dom";
import { Home } from "./Home";

function send(method, url, data, callback){
    var xhr = new XMLHttpRequest();
    xhr.onload = function() {
        if (xhr.status !== 200) callback("[" + xhr.status + "]" + xhr.responseText, null);
        else callback(null, JSON.parse(xhr.responseText));
    };
    xhr.open(method, url, true);
    if (!data) xhr.send();
    else{
        xhr.setRequestHeader('Content-Type', 'application/json');
        xhr.send(JSON.stringify(data));
    }
}

export class Signup extends Component {
  state = {
    username: "",
    password: "",
    redirectState: false
  };

  onChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  submitForm = e => {
    e.preventDefault();
    console.log(this.state);
    if (this.state.password.length >= 8 && this.state.password.length > 0) {
      this.setState({ username: "", password: "" });
      this.props.history.push("/Home");
    } else {
      this.setState({ password: "" });
      alert("Password has to be at least 8 characters!");
    }
    fetch('http://localhost:3001/user/signup', {
        method: 'POST',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({
            username: this.state.username,
            secondParam: this.state.password,
        })
    })
    .then((resp) => resp.json())
    .then(function(data){
        console.log(data.json());
    });
  };

  render() {
    // const redirectState = this.state.redirectState;
    // if (redirectState === true) {
    //   return (
    //     <BrowserRouter>
    //       <Route path="/" Component={Home} />
    //     </BrowserRouter>
    //   );
    // }

    return (
      <div className="wrapper">
        <div className="form-wrapper">
          <h1>Create Account</h1>
          <form
            id="create-account-form"
            onSubmit={this.submitForm.bind(this)}
            noValidate
          >
            <div className="username">
              <label htmlFor="username"> username </label>
              <input
                type="text"
                placeholder="Username"
                name="username"
                value={this.state.username}
                onChange={e => this.onChange(e)}
              />
            </div>

            <div className="password">
              <label htmlFor="password"> Password </label>
              <input
                placeholder="Password"
                type="password"
                name="password"
                value={this.state.password}
                onChange={e => this.onChange(e)}
              />
            </div>

            <div className="createAccount">
              <button type="submit">Create Account</button>
            </div>
          </form>
        </div>
      </div>
    );
  }
}
